document.addEventListener('deviceready', ModelReady, false);

 function ModelReady () {
 		window.db = window.openDatabase("first90_db", "1.0", "first90", 10000000);
 		if( localStorage.getItem( 'databaseSetup' ) == null ) {
 			CreateTable(SetUpTables); 
 		}
}

function SetUpTables ( tx ) {
    console.log('setting up tables');
	//tx.executeSql('DROP TABLE IF EXISTS ARTICLES'); // REMOVE ME!
    tx.executeSql('CREATE TABLE IF NOT EXISTS WEEKS (ID INTEGER PRIMARY KEY, WEEKTITLE TEXT, VIDEOTITLE TEXT, VIDEO TEXT)');
	tx.executeSql('CREATE TABLE IF NOT EXISTS ARTICLES (ID INTEGER PRIMARY KEY AUTOINCREMENT, TITLE TEXT, CONTENT TEXT, WEEK INTEGER, DAY INTEGER, FAVORITE INTEGER, COMPLETED INTEGER, TYPE TEXT)');
    tx.executeSql('CREATE INDEX ARTICLETEXT ON ARTICLES(CONTENT)');
	//tx.executeSql ('CREATE TABLE IF NOT EXISTS META (ID INTEGER, ARTICLE INTEGER, CONTENT INTEGER)');
	//tx.executeSql ( 'CREATE TABLE IF NOT EXISTS TAGS (ID INTEGER, TAG TEXT)' );
	PopulateArticles( tx );
	window.localStorage.setItem( 'databaseSetup', '1' );
}

	/* Takes in table name and a list of columns/variable types
	 * args should be bundled together and seperated by a space i.e. id BIGINT, name VARCHAR(20) */
function CreateTable ( table ) {		
	db.transaction ( table, errorCB, successCB );
}

function errorCB (err) {
		alert('Error processing SQL:  msg [' + err.message + '] ( code [' + err.code + '] )');
}
	
function successCB ( ) {
		//alert( 'success!' );
}

