function SaveSetupData ( startDate, notification ) {
	window.localStorage.setItem( 'not', notification );
	window.localStorage.setItem( 'startDate', startDate );
}

function SaveCurrentWeek ( week ) {
	localStorage.setItem( 'curWeek', week );
}

function SaveCurrentDay ( day ) {
	localStorage.setItem( 'curDay', day );
}

function GetCurrentWeek ( ) {
	var curWeek = localStorage.getItem( 'curWeek') 
	if( curWeek == "") {
		return 0;
	} else {
		return curWeek;
	}
}

function GetCurrentDay ( ) {
	return localStorage.getItem( 'curDay' );
}

//Load Setup Data on Application Start
function LoadSetupData () {
	db.transaction( SetupQuery, errorCB );
}

function SetupQuery ( tx ) {
	tx.executeSql("SELECT * FROM ARTICLES WHERE TYPE = 'setup'", [], LoadSetupDataIntoLocalStorage);
}

function LoadSetupDataIntoLocalStorage ( tx, results ) {
	if( results.rows.length > 0 ) {
		window.localStorage.setItem('not', results.rows.item(0).CONTENT );
		window.localStorage.setItem('startDate', results.rows.item(0).UPDATED );
	}
}
// end Load Setup

