// Gets a list of articles in the array of article ids and when finished calls callback with the results
function GetArticles(articles, callback) {
    db.transaction(function(tx) {
                   var query = 'SELECT * FROM ARTICLES WHERE ID in (' + articles + ')';
                   //alert('our query is [' + query + ']');
                   tx.executeSql(query, [], function(tx, results) {
                                 // NOTE: execution reaches here second ********
                                 //alert('found #1' + results.rows.item(0)); //*
                                 callback(results);                          //*
                                 });                                         //*
                   }, errorCB);                                              //*
    // NOTE: execution reaches here first **************************************
    //alert('leaving GetArticles()');
}

//Get Articles for list.
function GetArticlesListForWeek ( week, type ) {
	db.transaction( function(tx) {
                      // I've added a join here on the WEEKS table so that we can get the WEEKTITLE for displaying on the week page.  Issue #13.
                      var query  = 'SELECT w.WEEKTITLE, w.VIDEO, w.VIDEOTITLE, a.* FROM ARTICLES a, WEEKS w WHERE w.ID = a.WEEK AND TYPE = \'' + type + '\' AND WEEK = ' + week + ' ORDER BY DAY ASC  LIMIT 5 ';
                      tx.executeSql( query, [], page.init.populateWeek );
                    }, errorCB );
}

