//Search Query 
function SearchQuery ( searchString ) {
	var queryPart =  FormatSearchQuery( searchString );
	db.transaction( function ( tx ) {
			var query = 'SELECT w.WEEKTITLE, a.* FROM ARTICLES a, WEEKS w WHERE a.WEEK = w.ID' + queryPart;
			tx.executeSql( query, [], page.init.renderResults );
		}, errorCB );
}

//splits multiple search terms into their own statement
function FormatSearchQuery ( searchString ) {
	var terms = searchString.toString().split( ' ' );
	var queryPart = ' ';
	for( var i = 0; i < terms.length; i++ ){
		queryPart += ' AND CONTENT LIKE "%' +terms[ i ] + '%"';
	}
	console.log(queryPart);
	return queryPart;
}

