	//Week 1 -- Secondary Content
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Reach the Break-Even Point-Fast', 'secondary','A%20survey%20asked%20more%20than%20two%20hundred%20CEOs%20and%20presidents%20for%20their%20best%20estimates%20of%20how%20long%20it%20takes%20a%20typical%20midlevel%20leader%20in%20their%20organizations%20to%20reach%20the%20break-even%20point.%20The%20average%20of%20their%20responses%20was%20%3Cstrong%3E6.2%20months%3C%2Fstrong%3E.%20Of%20course%2C%20there%u2019s%20a%20lot%20of%20variation%20in%20how%20long%20it%20takes%20individual%20managers%20to%20reach%20break-even%2C%20depending%20on%20their%20situation.%20For%20instance%3A%0A%0A%26nbsp%3B%0A%3Cul%3E%0A%09%3Cli%3EIf%20you%u2019ve%20inherited%20a%20complete%20disaster%2C%20you%20might%20start%20creating%20value%20%28in%20the%20form%20of%20relief%20and%20renewed%20energy%29%20from%20the%20instant%20your%20appointment%20is%20announced.%3C%2Fli%3E%0A%09%3Cli%3EIf%20you%u2019ve%20been%20hired%20from%20the%20outside%20into%20a%20very%20successful%20organization%2C%20you%20might%20need%20more%20time%20to%20become%20a%20net%20value%20contributor.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%26nbsp%3B%0A%0AWhatever%20your%20situation%2C%20you%u2019ll%20want%0A%0Ato%20reach%20the%20break-even%20point%20as%20fast%20as%20possible.%20What%20you%20do%20in%20your%20first%20ninety%20days%20can%20make%20a%20big%20difference.%20And%20guess%20what%3F%20Independent%20research%20shows%20that%20%3Cstrong%3Ethe%3C%2Fstrong%3E%20%3Cstrong%3Eideas%20and%20practices%20you%u2019ll%20discover%20in%20this%20application%20will%20help%20you%20%3Cem%3Ereduce%3C%2Fem%3E%20the%20time%20it%20takes%20to%20reach%20break-even%20%3Cem%3Eby%3C%2Fem%3E%3C%2Fstrong%3E%20%3Cstrong%3E%3Cem%3Eas%20much%20as%2040%20percent%3C%2Fem%3E%3C%2Fstrong%3E.%3Cstrong%3E%3C%2Fstrong%3E', '1', '1' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Assess Your Vulnerability to Transition Traps', 'secondary','It%u2019s%20all%20too%20easy%20to%20step%20on%20land%20mines%20when%20you%u2019re%20transitioning%20into%20a%20new%20role.%20But%20the%20more%20you%20know%20about%20the%20most%20common%20traps%20new%20leaders%20fall%20into%2C%20the%20greater%20your%20chances%20of%20avoiding%20them.%20So%20let%u2019s%20take%20a%20closer%20look%20at%20some%20classic%20transition%20traps.%0A%0A%3Cem%3EInstructions%3A%3C%2Fem%3E%0A%0AAs%20you%20consider%20the%20following%20list%2C%20think%20about%20your%20vulnerability%20to%20each%20trap%20on%20a%20scale%20from%201%20to%2010.%20%u201C1%u201D%20means%20%u201Cnot%20at%20all%20vulnerable%u201D%3B%20%u201C10%u201D%20means%20%u201Cextremely%20vulnerable.%u201D%20For%20traps%20where%20you%20consider%20yourself%A0%20%u201Cextremely%20vulnerable%2C%u201D%20think%20about%20ways%20you%20can%20improve%20your%20approach.%0A%3Col%20start%3D%221%22%3E%0A%09%3Cli%3E%3Cstrong%3ESticking%20with%20what%20you%20know.%20%3C%2Fstrong%3EYou%20take%20the%20same%20approach%20or%20try%20to%20use%20the%20same%20strengths%20that%20worked%20in%20your%20previous%20roles.%0A%3Cul%3E%0A%09%3Cli%3E%3Cem%3EConsequences%3A%3C%2Fem%3E%20You%20fail%20to%20develop%20%A0%A0the%20different%20competencies%20needed%20for%20success%20in%20your%20new%20role.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3C%2Fli%3E%0A%3C%2Fol%3E%0A%3Col%20start%3D%222%22%3E%0A%09%3Cli%3E%3Cstrong%3EFalling%20prey%20to%20%u201Cthe%20action%20imperative.%u201D%3C%2Fstrong%3EYou%20feel%20compelled%20to%20take%20immediate%20action%2C%20give%20the%20impression%20that%20you%u2019re%20decisive%2C%20or%20put%20your%20own%20stamp%20on%20the%20organization.%0A%3Cul%3E%0A%09%3Cli%3E%3Cem%3EConsequences%3A%3C%2Fem%3E%20You%20don%u2019t%20take%20time%20to%20learn%20about%20your%20new%20situation.%20You%20make%20bad%20decisions.%20You%20catalyze%20resistance%20to%20your%20initiatives.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3C%2Fli%3E%0A%3C%2Fol%3E%0A%3Col%20start%3D%223%22%3E%0A%09%3Cli%3E%3Cstrong%3EFailing%20to%20clarify%20expectations.%3C%2Fstrong%3EYou%20don%u2019t%20establish%20clear%20objectives%20with%20your%20boss%20and%20other%20key%20stakeholders.%0A%3Cul%3E%0A%09%3Cli%3E%3Cem%3EConsequences%3A%3C%2Fem%3E%20You%20don%u2019t%20meet%20others%u2019%20expectations%2C%20even%20if%20you%u2019re%20performing%20well.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3C%2Fli%3E%0A%3C%2Fol%3E%0A%3Col%20start%3D%224%22%3E%0A%09%3Cli%3E%3Cstrong%3ETrying%20to%20do%20too%20much.%3C%2Fstrong%3EYou%20launch%20many%20initiatives%2C%20hoping%20that%20at%20least%20some%20will%20pay%20off.%0A%3Cul%3E%0A%09%3Cli%3E%3Cem%3EConsequences%3A%3C%2Fem%3E%20You%20confuse%20people.%20Resources%20get%20fragmented%20across%20too%20many%20efforts.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3C%2Fli%3E%0A%3C%2Fol%3E%0A%3Col%20start%3D%225%22%3E%0A%09%3Cli%3E%3Cstrong%3EComing%20in%20with%20%u201Cthe%20answer.%u201D%3C%2Fstrong%3EYou%20assume%20you%20know%20what%20problems%20your%20new%20organization%20has%20and%20how%20to%20fix%20them.%0A%3Cul%3E%0A%09%3Cli%3E%3Cem%3EConsequences%3A%3C%2Fem%3E%20You%20make%20wrong%20assumptions.%20You%20alienate%20people%20who%20could%20help%20you%20understand%20what%u2019s%20going%20on.%20You%20lose%20their%20support%20for%20crafting%20good%20solutions.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3C%2Fli%3E%0A%3C%2Fol%3E%0A%3Col%20start%3D%226%22%3E%0A%09%3Cli%3E%3Cstrong%3EEngaging%20in%20the%20wrong%20type%20of%20learning.%3C%2Fstrong%3EYou%20focus%20too%20much%20on%20technical%20aspects%20of%20the%20business%20%28strategies%2C%20customers%2C%20markets%29%20and%20too%20little%20on%20the%20cultural%20and%20political%20landscapes.%0A%3Cul%3E%0A%09%3Cli%3E%3Cem%3EConsequences%3A%3C%2Fem%3E%20You%20lack%20the%20insights%20and%20information%20needed%20to%20understand%20what%u2019s%20really%20going%20on%20in%20the%20organization%20and%20how%20to%20drive%20positive%20change.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3C%2Fli%3E%0A%3C%2Fol%3E%0A%3Col%20start%3D%227%22%3E%0A%09%3Cli%3E%3Cstrong%3ENeglecting%20%u201Chorizontal%u201D%20relationships.%20%3C%2Fstrong%3EYou%20focus%20on%20nurturing%20good%20relationships%20with%20your%20boss%20and%20direct%20reports%2C%20ignoring%20peers%20and%20other%20stakeholders.%0A%3Cul%3E%0A%09%3Cli%3E%3Cem%3EConsequences%3A%3C%2Fem%3E%20You%20don%u2019t%20fully%20grasp%20what%20it%20will%20take%20to%20succeed%20in%20your%20new%20role.%20You%20miss%20early%20opportunities%20to%20build%20supportive%20alliances.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3C%2Fli%3E%0A%3C%2Fol%3E', '1', '2' )");
