	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'How Prepared Are You?', 'secondary','%3Cem%3EInstructions%3A%3C%2Fem%3E%0A%0ACreate%20a%20document%20recording%20your%20thoughts%20about%20the%20following%20questions%3A%0A%0A%26nbsp%3B%0A%3Col%3E%0A%09%3Cli%3EIf%20you%u2019ve%20been%20promoted%2C%20what%20are%20the%20implications%20for%20how%20you%20should%20balance%20breadth%20and%20depth%2C%20delegate%2C%20influence%2C%20communicate%2C%20exhibit%20%u201Cleadership%20presence%2C%u201D%20and%20manage%20any%20direct%20reports%20who%20used%20to%20be%20your%20peers%3F%3C%2Fli%3E%0A%09%3Cli%3EIf%20you%u2019re%20joining%20a%20new%20organization%2C%20how%20will%20you%20orient%20yourself%20to%20the%20business%2C%20identify%20and%20connect%20with%20key%20stakeholders%2C%20clarify%20expectations%2C%20and%20adapt%20to%20the%20new%20culture%3F%20What%u2019s%20the%20right%20balance%20between%20adapting%20to%20the%20new%20situation%20and%20trying%20to%20alter%20it%3F%3C%2Fli%3E%0A%09%3Cli%3EIf%20you%u2019re%20moving%20between%20units%20in%20the%20same%20organization%20or%20making%20an%20international%20move%2C%20what%20are%20the%20biggest%20cultural%20differences%20you%u2019ll%20be%20experiencing%3F%20What%20could%20you%20do%20to%20adapt%20to%20the%20new%20culture%20you%u2019re%20transitioning%20into%3F%3C%2Fli%3E%0A%09%3Cli%3EAre%20there%20any%20aspects%20of%20your%20new%20job%20that%20are%20critical%20to%20success%20but%20that%20you%20prefer%20not%20to%20focus%20on%3F%20If%20so%2C%20why%20is%20that%20the%20case%3F%20How%20will%20you%20compensate%20for%20these%20potential%20blind%20spots%3F%3C%2Fli%3E%0A%09%3Cli%3EWhat%20do%20you%20need%20to%20do%20to%20make%20the%20mental%20leap%20into%20your%20new%20position%3F%20Who%20could%20give%20you%20advice%20and%20counsel%20on%20this%3F%20What%20other%20activities%20might%20help%20you%20do%20this%3F%3C%2Fli%3E%0A%3C%2Fol%3E', '2', '5' )");
	
