	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Define Your Learning Agenda', 'primary','As%20you%20saw%20yesterday%2C%20the%20best%20way%20to%20combat%20learning%20disabilities%20is%20to%20display%20a%20genuine%20desire%20to%20learn%20about%20the%20new%20organization%20you%u2019re%20transitioning%20into.%20Start%20by%20compiling%20a%20list%20of%20questions%20about%20the%20organization.%20These%20questions%20constitute%20your%20%3Cstrong%3Elearning%20agenda%3C%2Fstrong%3E%3A%20what%20you%20need%20to%20know%20about%20the%20organization%20to%20lead%20it%20effectively.%0A%0A%26nbsp%3B%0A%0ATo%20craft%20your%20learning%20agenda%2C%20you%u2019ll%20need%20to%20investigate%20the%20following%3A%0A%0A%26nbsp%3B%0A%3Cul%3E%0A%09%3Cli%3E%3Cstrong%3EYour%20organization%u2019s%3C%2Fstrong%3E%20%3Cstrong%3Epast%2C%20present%2C%20and%20future.%20%3C%2Fstrong%3EWhy%20were%20things%20set%20up%20the%20way%20they%20were%3F%20Are%20those%20reasons%20still%20valid%20today%3F%20Do%20changes%20in%20the%20business%20environment%20suggest%20that%20some%20things%20should%20be%20done%20differently%20in%20the%20future%3F%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3ETechnical%20concerns.%20%3C%2Fstrong%3EWhat%20unfamiliar%20markets%2C%20technologies%2C%20processes%2C%20and%20systems%20will%20you%20need%20to%20grapple%20with%20to%20succeed%20in%20your%20new%20role%3F%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3ECultural%20questions.%3C%2Fstrong%3E%20How%20do%20the%20norms%2C%20values%2C%20and%20behavioral%20expectations%20in%20your%20new%20organization%20differ%20from%20those%20in%20your%20previous%20organization%3F%20What%20do%20these%20differences%20imply%20for%20how%20you%20might%20best%20approach%20your%20new%20role%3F%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3Cstrong%3EPolitical%20acumen.%20%3C%2Fstrong%3EWhat%20hard-to-see%20decision-making%20process%20and%20alliances%20exist%20in%20the%20shadow%20of%20the%20organization%u2019s%20formal%20structure%3F%20How%20might%20you%20navigate%20through%20these%20dynamics%3F', '3', '2' )");
