	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Assess How You''re Doing', 'secondary','%3Cem%3EInstructions%3A%20%3C%2Fem%3ECreate%20a%20document%20that%20shows%20your%20responses%20to%20the%20following%20questions%3A%0A%0A%26nbsp%3B%0A%3Col%3E%0A%09%3Cli%3EHow%20effective%20are%20you%20at%20learning%20about%20new%20organizations%3F%20If%20you%20feel%20you%20could%20be%20more%20effective%2C%20what%20are%20some%20steps%20you%20could%20take%20to%20do%20so%3F%3C%2Fli%3E%0A%09%3Cli%3EWhat%20is%20your%20learning%20agenda%3F%20Based%20on%20what%20you%20know%20now%2C%20what%20questions%20would%20best%20guide%20your%20early%20inquiry%3F%3C%2Fli%3E%0A%09%3Cli%3EIf%20you%u2019ve%20begun%20forming%20hypotheses%20about%20what%u2019s%20going%20on%20in%20your%20new%20situation%2C%20what%20are%20they%3F%20How%20will%20you%20test%20them%3F%3C%2Fli%3E%0A%09%3Cli%3EGiven%20the%20questions%20you%20want%20to%20answer%2C%20who%20is%20most%20likely%20to%20provide%20you%20with%20the%20best%20insights%3F%3C%2Fli%3E%0A%09%3Cli%3EHow%20might%20you%20make%20your%20learning%20process%20more%20efficient%3F%3C%2Fli%3E%0A%09%3Cli%3EWhat%20support%20is%20available%20to%20you%20to%20accelerate%20the%20learning%20process%3F%20How%20might%20you%20best%20leverage%20it%3F%3C%2Fli%3E%0A%3C%2Fol%3E', '3', '5' )");
	
