	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Understand Your STARS Situation', 'primary','In%20your%20new%20role%2C%20you%20may%20be%20called%20on%20to%20manage%20a%20number%20of%20business%20situations.%20The%20STARS%20acronym%20captures%20these%20situations%3A%0A%0A%26nbsp%3B%0A%3Cul%3E%0A%09%3Cli%3E%3Cstrong%3E%3Cspan%20style%3D%22text-decoration%3A%20underline%3B%22%3ES%3C%2Fspan%3Etart-up%3C%2Fstrong%3E%u2014launching%20an%20entirely%20new%20venture.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3E%3Cspan%20style%3D%22text-decoration%3A%20underline%3B%22%3ET%3C%2Fspan%3Eurnaround%3C%2Fstrong%3E%u2014getting%20a%20struggling%20business%20back%20on%20track.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3E%3Cspan%20style%3D%22text-decoration%3A%20underline%3B%22%3EA%3C%2Fspan%3Eccelerated%20growth%3C%2Fstrong%3E%u2014dealing%20with%20rapid%20expansion%20in%20a%20business.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3E%3Cspan%20style%3D%22text-decoration%3A%20underline%3B%22%3ERe%3C%2Fspan%3Ealignment%3C%2Fstrong%3E%u2014reenergizing%20a%20once-leading%20business%20that%u2019s%20now%20facing%20serious%20problems.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3E%3Cspan%20style%3D%22text-decoration%3A%20underline%3B%22%3ES%3C%2Fspan%3Eustaining%20success%3C%2Fstrong%3E%u2014preserving%20the%20vitality%20of%20a%20successful%20organization%20and%20taking%20it%20to%20the%20next%20level.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%26nbsp%3B%0A%0AEach%20of%20these%20business%20situations%20can%20describe%20an%20entire%20organization%3B%20a%20division%2C%20business%20unit%2C%20or%20function%3B%20or%20a%20project%20or%20initiative.%20Each%20can%20arise%20for%20leaders%20at%20any%20level.%20For%20instance%2C%20here%20are%20examples%20of%20what%20different%20kinds%20of%20%3Cstrong%3Estart-up%3C%2Fstrong%3E%20situations%20might%20look%20like%3A%0A%0A%26nbsp%3B%0A%3Cul%3E%0A%09%3Cli%3EA%20frontline%20supervisor%20starts%20a%20new%20production%20line.%3Cstrong%3E%3C%2Fstrong%3E%3C%2Fli%3E%0A%09%3Cli%3EA%20brand%20manager%20launches%20a%20new%20product.%3Cstrong%3E%3C%2Fstrong%3E%3C%2Fli%3E%0A%09%3Cli%3EA%20general%20manager%20initiates%20a%20new%20business.%3C%2Fli%3E%0A%09%3Cli%3EA%20CEO%20takes%20over%20a%20company%20that%u2019s%20in%20start-up%20mode.%3Cstrong%3E%3C%2Fstrong%3E%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3Cstrong%3E%A0%3C%2Fstrong%3E%0A%0AAs%20in%20start-ups%2C%20the%20other%20four%20STARS%20situations%20can%20also%20arise%20for%20leaders%20at%20all%20levels%2C%20in%20companies%20large%20and%20small.%0A%0A%26nbsp%3B%0A%0AEach%20STARS%20situation%20also%20has%20a%20unique%20mix%20of%20opportunities%20and%20challenges.%20And%20each%20situation%20demands%20specific%20leadership%20skills%20and%20styles.%0A%0A%26nbsp%3B%0A%0A%3Cem%3EWhat%u2019s%20%3Cstrong%3Eyour%20%3C%2Fstrong%3Enew%3Cstrong%3E%20%3C%2Fstrong%3Ebusiness%20situation%3F%3C%2Fem%3E', '4', '1' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Diagnose Your STARS Portfolio', 'primary','Your%20new%20situation%20isn%u2019t%20likely%20to%20be%20a%20pure%20and%20neat%20example%20of%20a%20start-up%2C%20turnaround%2C%20accelerated-growth%2C%20realignment%2C%20or%20sustaining-success%20situation.%20At%20a%20high%20level%2C%20your%20situation%20may%20fit%20generally%20into%20one%20of%20these%20categories.%20But%20when%20you%20drill%20down%2C%20you%u2019ll%20almost%20certainly%20find%20that%20you%u2019re%20managing%20a%20portfolio%20of%20STARS%20situations.%20For%20instance%3A%0A%0A%26nbsp%3B%0A%3Cul%3E%0A%09%3Cli%3EYou%u2019re%20rescuing%20a%20troubled%20division%20%28%3Cstrong%3E%3Cem%3Eturnaround%3C%2Fem%3E%3C%2Fstrong%3E%29%20that%20nevertheless%20has%20several%20high-performing%2C%20state-of-the-art%20plants%20%28%3Cstrong%3E%3Cem%3Esustaining%20success%3C%2Fem%3E%3C%2Fstrong%3E%29.%3C%2Fli%3E%0A%09%3Cli%3EYou%u2019re%20taking%20over%20a%20company%20that%20has%20incremental%20growth%20with%20successful%20products%20%28%3Cstrong%3E%3Cem%3Esustaining%20success%3C%2Fem%3E%3C%2Fstrong%3E%29%2C%20and%20one%20group%20in%20the%20company%20is%20launching%20a%20new%20product%20line%20based%20on%20a%20new%20technology%20%28%3Cstrong%3E%3Cem%3Estart-up%3C%2Fem%3E%3C%2Fstrong%3E%29.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%26nbsp%3B%0A%0ATo%20succeed%20in%20your%20new%20role%2C%20you%20have%20to%20diagnose%20your%20STARS%20portfolio.%20That%20means%20figuring%20out%20which%20pieces%20of%20your%20new%20responsibilities%20%28products%2C%20processes%2C%20projects%2C%20plants%2C%20people%29%20fall%20into%20which%20of%20the%20five%20STARS%20business-situation%20categories.%0A%0A%26nbsp%3B%0A%0AWhy%20diagnose%20your%20STARS%20portfolio%3F%20The%20process%3A%0A%0A%26nbsp%3B%0A%3Cul%3E%0A%09%3Cli%3EHelps%20you%20figure%20out%20how%20you%u2019ll%20need%20to%20manage%20the%20various%20pieces%20differently.%3C%2Fli%3E%0A%09%3Cli%3EEnables%20you%20to%20anticipate%20the%20challenges%20and%20opportunities%20you%u2019ll%20encounter%20with%20each%20piece.%3C%2Fli%3E%0A%09%3Cli%3EGives%20you%20a%20common%20language%20with%20which%20to%20talk%20with%20your%20new%20boss%2C%20peers%2C%20and%20direct%20reports%20about%20why%20and%20how%20you%u2019re%20going%20to%20manage%20various%20pieces%20differently.%3C%2Fli%3E%0A%09%3Cli%3EHelps%20you%20decide%20what%20kind%20of%20leader%20you%u2019ll%20need%20to%20be%20for%20each%20piece%20in%20your%20portfolio.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%0A%3Cdiv%20id%3D%22read-more%22%3E%0AFor%20more%20on%20understanding%20your%20STARS%20portfolio%20see%20Chapter%203%20in%20the%2010th%20anniversary%20edition%20of%20The%20First%2090%20Days.%0A%3C%2Fdiv%3E', '4', '2' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Decide How You''ll Lead Change', 'primary','As%20you%20discovered%20yesterday%2C%20diagnosing%20your%20STARS%20portfolio%20helps%20you%20figure%20out%20the%20mix%20of%20STARS%20situations%20you%20are%20managing.%20You%20need%20to%20manage%20each%20piece%20in%20your%20portfolio%20differently.%20That%20means%20leading%20change.%0A%0A%26nbsp%3B%0A%0ATo%20determine%20how%20you%20will%20lead%20change%2C%20you%20need%20to%20ask%20yourself%20five%20questions%20regarding%20each%20piece%20in%20your%20STARS%20portfolio%3A%0A%0A%26nbsp%3B%0A%3Cul%3E%0A%09%3Cli%3E%3Cstrong%3E%u201CWhat%u2019s%20my%20strategic%20intent%3F%u201D%20%3C%2Fstrong%3EWill%20you%20need%20to%20perform%20immediate%2C%20radical%20surgery%20on%20organizational%20structures%20or%20strategies%3F%20Or%20merely%20fine-tune%20certain%20systems%20and%20skills%3F%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3E%u201CWhere%20can%20I%20score%20early%20wins%3F%u201D%20%3C%2Fstrong%3EWill%20closing%20an%20ailing%20plant%20and%20cutting%20unnecessary%20projects%20help%20you%20quickly%20establish%20successes%20that%20can%20build%20momentum%3F%20Or%20would%20the%20best%20early%20wins%20consist%20of%20raising%20people%u2019s%20awareness%20of%20the%20need%20for%20change%3F%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3E%u201CWhat%20will%20the%20right%20leadership%20team%20look%20like%3F%u201D%20%3C%2Fstrong%3EWould%20it%20be%20best%20to%20clean%20house%20at%20the%20top%20of%20your%20organization%20and%20recruit%20replacements%20from%20outside%3F%20Dismiss%20leaders%20who%20are%20resisting%20change%20and%20replace%20them%20with%20people%20from%20within%20the%20organization%3F%20Promote%20or%20bring%20in%20people%20to%20the%20team%20who%20have%20certain%20critical%20skills%3F%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3E%u201CHow%20can%20I%20forge%20supporting%20alliances%3F%u201D%20%3C%2Fstrong%3EWhose%20support%20will%20you%20most%20need%20to%20make%20change%20happen%20in%20each%20part%20of%20your%20STARS%20portfolio%3F%20What%20do%20you%20need%20them%20to%20do%3F%20When%20do%20you%20need%20them%20to%20do%20it%3F%3C%2Fli%3E%0A%3C%2Ful%3E', '4', '3' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Adapt Your Leadership Style to Your Situation', 'primary','Different%20business%20situations%20require%20different%20leadership%20styles.%20For%20example%3A%0A%0A%26nbsp%3B%0A%3Cul%3E%0A%09%3Cli%3EIn%20%3Cstrong%3Eturnarounds%3C%2Fstrong%3E%2C%20you%u2019re%20often%20dealing%20with%20people%20who%20are%20hungry%20for%20hope%2C%20vision%2C%20and%20direction.%20This%20calls%20for%20%3Cstrong%3E%3Cem%3Eheroic%3C%2Fem%3E%3C%2Fstrong%3E%3Cem%3E%20%3C%2Fem%3Eleadership%2C%20where%20you%20charge%20against%20the%20enemy%2C%20sword%20in%20hand.%20The%20hero%20must%20take%20quick%2C%20decisive%20action%2C%20often%20with%20incomplete%20information.%20The%20good%20news%20is%20that%20people%20line%20up%20behind%20heroes%20in%20times%20of%20trouble%20and%20follow%20their%20lead.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3ERealignments%20%3C%2Fstrong%3Ecall%20for%20%3Cstrong%3E%3Cem%3Estewardship%3C%2Fem%3E%3C%2Fstrong%3E%3Cem%3E%20%3C%2Fem%3Eor%3Cem%3E%20%3Cstrong%3Eservant%20leadership%3C%2Fstrong%3E%3C%2Fem%3E.%20You%20must%20be%20diplomatic%20and%20patient%2C%20building%20consensus%20around%20the%20need%20for%20change%20by%20promoting%20shared%20diagnosis%20of%20problems%2C%20influencing%20opinion%20leaders%2C%20and%20encouraging%20benchmarking.%20You%20need%20a%20deep%20understanding%20of%20your%20organization%u2019s%20culture%20and%20political%20landscape.%20And%20you%20must%20take%20a%20systematic%20approach%20to%20deciding%20which%20people%2C%20processes%2C%20and%20other%20resources%20you%u2019ll%20preserve%20and%20which%20you%u2019ll%20discard.%3C%2Fli%3E%0A%3C%2Ful%3E', '4', '4' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Manage Yourself to Adapt Your Style', 'primary','We%20all%20have%20leadership%20styles%20that%20feel%20more%20natural%20to%20us.%20But%20leading%20successfully%20requires%20the%20ability%20to%20adapt%20your%20leadership%20style%20to%20your%20business%20situation.%0A%0A%26nbsp%3B%0A%0ATo%20do%20this%2C%20you%20must%20excel%20at%20three%20aspects%20of%20self-management%3A%0A%0A%26nbsp%3B%0A%3Cul%3E%0A%09%3Cli%3E%3Cstrong%3ESelf-awareness.%20%3C%2Fstrong%3EKnow%20which%20of%20your%20inherent%20leadership%20skills%20and%20styles%20will%20serve%20you%20well%20in%20a%20business%20situation%20in%20question%20and%20which%20are%20likely%20to%20get%20you%20into%20trouble.%20For%20instance%2C%20if%20you%u2019re%20usually%20ready%20to%20race%20to%20the%20rescue%2C%20sword%20in%20hand%2C%20that%20might%20be%20OK%20in%20a%20turnaround%20situation.%20But%20it%20could%20hurt%20you%20in%20a%20realignment%2C%20where%20building%20alliances%20is%20much%20more%20critical.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3EPersonal%20discipline.%3C%2Fstrong%3E%20Strive%20to%20focus%20your%20leadership%20energies%20on%20the%20right%20situations.%20For%20example%2C%20channel%20your%20heroic%20energy%20into%20the%20pieces%20of%20your%20STARS%20portfolio%20that%20most%20need%20it%20%28like%20turnaround%20situations%29%2C%20and%20turn%20it%20off%20in%20other%20pieces%20of%20your%20portfolio%20that%20would%20benefit%20from%20more%20servant-leadership%20energy%20%28such%20as%20realignments%29.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3EThe%20capacity%20to%20build%20complementary%20teams.%3C%2Fstrong%3E%20If%20you%20tend%20to%20adopt%20a%20more%20heroic%20leadership%20style%20and%20you%u2019re%20facing%20a%20situation%20calling%20for%20stewards%2C%20consider%20recruiting%20some%20natural%20stewards.%20You%20can%20turn%20to%20them%20for%20wise%20counsel%2C%20so%20you%20don%u2019t%20go%20off%20half-cocked.%20And%20you%20can%20delegate%20alliance-building%20efforts%20to%20them%20if%20needed.%3C%2Fli%3E%0A%3C%2Ful%3E', '4', '5' )");
	
	
