	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Analyze Pivotal People''s Motivators', 'secondary','%3Cem%3EInstructions%3A%20%3C%2Fem%3E%0A%0A%3Cem%3E%A0%3C%2Fem%3E%0A%3Col%3E%0A%09%3Cli%3ECreate%20a%20document%20in%20which%20you%20list%20the%20pivotal%20people%20in%20the%20influence%20landscape%20for%20a%20particular%20early-win%20initiative%20you%20have%20in%20mind.%3C%2Fli%3E%0A%09%3Cli%3EFor%20each%20pivotal%20person%20on%20your%20list%2C%20identify%20what%20you%20see%20as%20his%20or%20her%20intrinsic%20motivators%2C%20such%20as%20the%20need%20for%20recognition%2C%20control%2C%20power%2C%20affiliation%20through%20relationships%20with%20colleagues%2C%20or%20personal%20growth.%3C%2Fli%3E%0A%09%3Cli%3EFor%20each%20person%2C%20also%20identify%20any%20situational%20pressures%20you%20think%20he%20or%20she%20is%20under%2C%20such%20as%20certain%20business%20objectives%20or%20pressure%20from%20peers%20to%20adopt%20their%20opinions.%3C%2Fli%3E%0A%09%3Cli%3EFor%20each%20person%2C%20also%20document%20your%20thoughts%20about%20his%20or%20her%20perceptions%20of%20the%20choices%20that%20are%20available.%20For%20instance%2C%20perhaps%20he%20believes%20that%20the%20status%20quo%20is%20still%20a%20choice.%20Or%20maybe%20she%20thinks%20that%20the%20benefits%20you%u2019ve%20said%20your%20initiative%20will%20produce%20won%u2019t%20really%20materialize.%3C%2Fli%3E%0A%3C%2Fol%3E', '9', '4' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'A Closer Look at Social Influence', 'secondary', '%3Cp%3ESocial%20influence%20is%20a%20powerful%20tool%20for%20persuading%20others%20to%20support%20your%20early-win%20initiatives.%20But%20what%20is%20social%20influence%2C%20exactly%3F%20It%u2019s%20the%20impact%20of%20others%u2019%20opinions%20on%20us%20and%20the%20rules%20that%20society%20has%20established%20about%20how%20we%20should%20behave.%0A%3C%2Fp%3E%0A%3Cp%3EFor%20example%2C%20when%20people%20know%20that%20a%20highly%20respected%20person%20already%20supports%20an%20initiative%2C%20they%u2019ll%20tend%20to%20see%20that%20initiative%20as%20more%20attractive.%20So%20if%20you%20can%20persuade%20opinion%20leaders%20to%20make%20commitments%20of%20support%20and%20mobilize%20their%20own%20networks%2C%20you%u2019ll%20create%20a%20potent%20leveraging%20effect.%0A%3C%2Fp%3E%0A%3Cp%3ESocial%20influence%20works%20in%20lots%20of%20other%20ways%2C%20too.%20For%20instance%2C%20people%20will%20be%20more%20likely%20to%20support%20an%20initiative%20if%20doing%20so%20lets%20them%3A%0A%3C%2Fp%3E%3Cul%3E%0A%09%3Cli%3E%3Cstrong%3EStay%20true%20to%20their%20strongest%20values%20and%20beliefs.%3C%2Fstrong%3E%20When%20we%20support%20an%20idea%20that%20goes%20against%20our%20values%20and%20beliefs%2C%20we%20feel%20uncomfortable.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3EHonor%20their%20prior%20commitments%20and%20decisions.%3C%2Fstrong%3E%20Failure%20to%20honor%20commitments%20signals%20unreliability%2C%20which%20people%20don%u2019t%20like%20to%20see%20in%20themselves.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3EReciprocate%20favors.%3C%2Fstrong%3E%20Reciprocity%20is%20a%20strong%20social%20norm%2C%20and%20we%u2019re%20more%20likely%20to%20support%20someone%u2019s%20idea%20if%20that%20person%20has%20done%20favors%20for%20us%20in%20the%20past.%3C%2Fli%3E%0A%09%3Cli%3E%3Cstrong%3EPreserve%20their%20reputations.%3C%2Fstrong%3E%20If%20we%20think%20that%20supporting%20someone%u2019s%20idea%20will%20enhance%20our%20reputation%20%28or%20at%20least%20not%20erode%20it%29%2C%20we%u2019re%20more%20likely%20to%20give%20our%20support.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3Cp%3ESo%20given%20what%20you%20know%20about%20social%20influence%2C%20how%20might%20you%20use%20this%20tool%20to%20convince%20people%20in%20your%20organization%20that%20your%20early-win%20initiatives%20are%20worth%20supporting%3F%3C%2Fp%3E', '9', '5' )");

	
