	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Build Momentum', 'primary', '%3Cp%3EOn%20Tuesday%2C%20you%20learned%20about%20the%20second%20core%20proposition%20for%20transitions%3A%20that%20using%20systematic%20methods%20can%20help%20you%20reduce%20the%20likelihood%20of%20failing%20in%20your%20new%20role%20and%20boost%20the%20chances%20of%20reaching%20the%20break-even%20point%20faster.%0A%3C%2Fp%3E%0A%3Cp%3EToday%20is%20all%20about%20the%20third%20core%20proposition%3A%0A%3C%2Fp%3E%0A%3Cp%3E%3Cstrong%3EThe%20overriding%20goal%20in%20any%20transition%20is%20to%20build%20momentum%20by%20creating%20virtuous%20cycles%20that%20build%20your%20personal%20credibility%2C%20and%20by%20not%20getting%20caught%20in%20vicious%20cycles%20that%20damage%20your%20credibility.%3C%2Fstrong%3E%0A%3C%2Fp%3E%0A%3Cp%3ELeadership%20is%20ultimately%20about%20leverage.%20Effective%20leaders%20leverage%20themselves%u2014their%20ideas%2C%20energy%2C%20relationships%2C%20and%20influence%u2014to%20create%20new%20patterns%20in%20their%20organizations.%20But%20every%20leader%20is%20just%20one%20person%2C%20and%20one%20person%20can%20accomplish%20very%20little%20alone.%20The%20ability%20to%20leverage%20yourself%20rests%20in%20turn%20on%20others%u2019%20perceptions%20of%20your%20personal%20credibility%20as%20well%20as%20on%20your%20own%20demonstrated%20effectiveness.%20Small%20successes%20can%20help%20you%20generate%20%u201Cleadership%20capital%u201D%20that%20you%20can%20invest%20to%20produce%20larger%20returns.%20The%20underlying%20goal%20of%20the%20strategies%20you%u2019ve%20learned%20about%u2014whether%20securing%20early%20wins%2C%20creating%20alliances%2C%20or%20building%20your%20team%u2014is%20to%20build%20momentum%20and%20thus%20increase%20your%20leverage.%3C%2Fp%3E', '12', '3' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Manage Transitions as Leadership-Development Crucibles', 'primary', '%3Cp%3EYesterday%20you%20reviewed%20the%20third%20core%20proposition%20about%20transitions%u2014the%20need%20to%20build%20momentum%20by%20strengthening%20your%20personal%20credibility.%0A%3C%2Fp%3E%0A%3Cp%3ENow%20let%u2019s%20turn%20to%20the%20fourth%20core%20proposition%3A%0A%3C%2Fp%3E%0A%3Cp%3E%3Cstrong%3ETransitions%20are%20crucibles%20for%20leadership%20development%2C%A0and%20you%20need%20to%20manage%20them%20accordingly.%3C%2Fstrong%3E%0A%3C%2Fp%3E%0A%3Cp%3EThe%20work%20you%u2019ve%20done%20during%20the%20preceding%20weeks%20may%20have%20convinced%20you%20that%20a%20sink-or-swim%20approach%20to%20leadership%20development%20wastes%20time%2C%20energy%2C%20and%20talent.%20You%20should%20certainly%20use%20transitions%20into%20demanding%20new%20positions%20to%20challenge%20yourself%20as%20well%20as%20your%20up-and-coming%20leaders.%20But%20don%u2019t%20just%20hope%20that%20you%20and%20they%20will%20make%20it.%20Teach%20yourself%20and%20them%20the%20transition%20acceleration%20skills%20essential%20for%20gaining%20a%20fighting%20chance.%3C%2Fp%3E', '12', '4' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Adopt a Standard Framework for Transition Acceleration', 'primary','%3Cp%3E%0A%20%20%20%20Yesterday%20you%20explored%20the%20fourth%20core%20proposition%20regarding%20transitions%26%238212%3Bthat%20they%20should%20be%20managed%20as%20crucibles%20for%20leadership%20development%20instead%0A%20%20%20%20of%20treated%20as%20sink-or-swim%20situations.%0A%3C%2Fp%3E%0A%3Cp%3E%0A%20%20%20%20Today%20is%20all%20about%20the%20fifth%20and%20final%20core%20proposition%3A%0A%3C%2Fp%3E%0A%3Cp%3E%0A%20%20%20%20%3Cb%3EAdopting%20a%20standard%20framework%20for%20accelerating%20transitions%20%3C%2Fb%3E%0A%20%20%20%20%3Cb%3Ecan%20pay%20big%20dividends%20for%20organizations.%3C%2Fb%3E%0A%3C%2Fp%3E%0A%3Cp%20align%3D%22center%22%3E%0A%20%20%20%20%3Cb%3E%20%3C%2Fb%3E%0A%3C%2Fp%3E%0A%3Cp%3E%0A%20%20%20%20Take%20the%20time%20to%20figure%20out%20how%20many%20people%20transition%20into%20new%20management%20roles%20in%20your%20organization%20in%20a%20typical%20year.%20Then%20estimate%20how%20many%20other%0A%20%20%20%20people%20each%20transitioning%20manager%20affects.%20What%20would%20you%20estimate%20as%20the%20net%20annual%20cost%20of%20transition%20in%20your%20organization%3F%20What%20would%20it%20be%20worth%20to%0A%20%20%20%20reduce%20that%20cost%20by%20just%205%20percent%3F%20Keeping%20people%20in%20jobs%20longer%2C%20as%20attractive%20as%20that%20might%20seem%2C%20is%20not%20the%20answer.%20Good%20people%20get%20bored%20after%20a%20few%0A%20%20%20%20years%20and%20yearn%20for%20new%20challenges.%20The%20best%20approach%20is%20to%20help%20everyone%20in%20your%20organization%20accelerate%20every%20transition.%0A%3C%2Fp%3E%0A%3Cdiv%20id%3D%22read-more%22%3EFor%20more%20on%20transition%20acceleration%20see%20Chapter%2010%20in%20the%2010th%20anniversary%20edition%20of%20The%20First%2090%20Days.%3C%2Fdiv%3E', '12', '5' )");
	
	 
	//Week 12 - Secondary Content
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Assess Your Situation and Yourself', 'secondary','%3Cdiv%3E%0A%20%20%20%20%3Cp%3E%0A%20%20%20%20%20%20%20%20%3Ci%3EInstructions%3A%3C%2Fi%3E%0A%20%20%20%20%3C%2Fp%3E%0A%20%20%20%20%3Cp%3E%0A%20%20%20%20%20%20%20%20Create%20a%20document%20recording%20your%20thoughts%20about%20the%20following%20questions%3A%0A%20%20%20%20%3C%2Fp%3E%0A%20%20%20%20%3Cp%3E%0A%20%20%20%20%20%20%20%201.%20What%20do%20you%20see%20as%20the%20most%20important%20opportunities%20presented%20by%20the%20business%20situations%20you%26%238217%3Bre%20facing%20in%20the%20STARS%20portfolio%20for%20your%20current%0A%20%20%20%20%20%20%20%20transition%3F%0A%20%20%20%20%3C%2Fp%3E%0A%20%20%20%20%3Cp%3E%0A%20%20%20%20%20%20%20%202.%20What%20do%20you%20see%20as%20the%20most%20dangerous%20pitfalls%20presented%20by%20those%20business%20situations%3F%0A%20%20%20%20%3C%2Fp%3E%0A%20%20%20%20%3Cp%3E%0A%20%20%20%20%20%20%20%203.%20What%20do%20you%20consider%20the%20most%20important%20strength%20you%20bring%20to%20your%20transition%3F%0A%20%20%20%20%3C%2Fp%3E%0A%20%20%20%20%3Cp%3E%0A%20%20%20%20%20%20%20%204.%20What%26%238217%3Bs%20an%20example%20of%20how%20you%20could%20use%20this%20strength%20to%20capitalize%20on%20the%20opportunities%20presented%20by%20your%20transition%20and%20to%20avoid%20its%0A%20%20%20%20%20%20%20%20pitfalls%3F%0A%20%20%20%20%3C%2Fp%3E%0A%20%20%20%20%3Cp%3E%0A%20%20%20%20%20%20%20%205.%20What%20would%20you%20say%20is%20the%20most%20serious%20vulnerability%20you%20bring%20to%20your%20transition%3F%0A%20%20%20%20%3C%2Fp%3E%0A%20%20%20%20%3Cp%3E%0A%20%20%20%20%20%20%20%206.%20In%20what%20ways%20might%20this%20vulnerability%20put%20you%20at%20risk%20of%20failing%20in%20your%20new%20role%3F%0A%20%20%20%20%3C%2Fp%3E%0A%20%20%20%20%3Cp%3E%0A%20%20%20%20%20%20%20%207.%20What%20steps%20could%20you%20take%20now%20to%20address%20this%20vulnerability%3F%0A%20%20%20%20%3C%2Fp%3E%0A%3C%2Fdiv%3E', '12', '1' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Have You Reached the Break-Even Point?', 'secondary', '%3Cp%3E%3Cem%3EInstructions%3A%3C%2Fem%3E%0A%3C%2Fp%3E%0A%3Cp%3ECreate%20a%20document%20recording%20your%20thoughts%20about%20the%20following%20questions%3A%0A%3C%2Fp%3E%3Col%3E%0A%09%3Cli%3EHow%20many%20months%20have%20you%20been%20in%20your%20new%20role%3F%3C%2Fli%3E%0A%09%3Cli%3EWhat%20forms%20of%20value%20have%20you%20consumed%20so%20far%20during%20your%20current%20transition%3F%3C%2Fli%3E%0A%09%3Cli%3EWhat%20forms%20of%20value%20%28if%20any%29%20have%20you%20begun%20producing%20for%20your%20new%20organization%3F%3C%2Fli%3E%0A%09%3Cli%3EHave%20you%20reached%20the%20break-even%20point%20yet%u2014the%20point%20where%20you%u2019re%20contributing%20as%20much%20value%20to%20your%20organization%20as%20you%u2019ve%20consumed%20from%20it%3F%3C%2Fli%3E%0A%09%3Cli%3EIf%20you%20have%20reached%20the%20break-even%20point%20and%20you%u2019re%20only%20one%20or%20two%20months%20into%20your%20transition%2C%20what%20steps%20have%20you%20taken%20to%20accelerate%20the%20process%20of%20reaching%20break-even%3F%3C%2Fli%3E%0A%09%3Cli%3EIf%20you%20haven%u2019t%20yet%20reached%20the%20break-even%20point%2C%20what%20steps%20could%20you%20take%20to%20do%20so%3F%3C%2Fli%3E%0A%3C%2Fol%3E', '12', '2' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Personal Credibility', 'secondary','video::http://www.kaltura.com/p/506471/sp/50647100/download/entry_id/1_g33gt5rt/flavor/0_cnyzb6k5', '12', '3' )");
	tx.executeSql ( "INSERT INTO ARTICLES (TITLE, TYPE, CONTENT, WEEK, DAY ) VALUES ( 'Do You Work for a Sink-or-Swim Organization?', 'secondary', '%3Cp%3EIn%20my%20work%20on%20accelerating%20leadership%20transitions%2C%20I%20still%20encounter%20lots%20of%20organizations%20that%20just%20don%u2019t%20get%20it.%20They%20persist%2C%20against%20reasoned%20argument%20and%20data%2C%20to%20foster%20a%20sink-or-swim%20approach%20to%20leadership%20development.%20They%20hire%20and%20promote%20good%20people%20only%20to%20let%20them%20drown%20in%20the%20deep%20end%20of%20the%20pool.%20In%20the%20process%2C%20they%20damage%20the%20careers%20of%20high-potential%20managers%2C%20experience%20regrettable%20losses%20of%20talent%2C%20and%20weaken%20their%20leadership%20pipelines.%0A%3C%2Fp%3E%0A%3Cp%3EDoes%20your%20organization%20have%20a%20sink-or-swim%20culture%3F%20See%20if%20the%20following%20descriptions%20ring%20true%3A%0A%3C%2Fp%3E%3Cul%3E%0A%09%3Cli%3EWhile%20the%20company%20may%20do%20a%20reasonable%20job%20of%20deciding%20whom%20to%20recruit%20and%20promote%2C%20it%20does%20little%20or%20nothing%20to%20help%20leaders%20make%20successful%20transitions.%20Regardless%20of%20the%20magnitude%20of%20the%20challenges%20transitioning%20leaders%20face%2C%20they%20are%20left%20to%20figure%20out%20their%20new%20roles%20by%20themselves.%3C%2Fli%3E%0A%09%3Cli%3ESenior%20leadership%20believes%20that%20it%u2019s%20best%20to%20test%20leaders%20by%20fire%20in%20order%20to%20separate%20the%20good%20from%20the%20bad.%20Respected%20executives%20perceive%20efforts%20to%20support%20leaders%20making%20transitions%20as%20%u201Ccoddling%u201D%20or%20worry%20that%20it%20will%20blunt%20the%20company%u2019s%20edge%20in%20some%20way.%3C%2Fli%3E%0A%09%3Cli%3EThe%20organization%20makes%20little%20effort%20to%20help%20leaders%20hired%20from%20the%20outside%20understand%20the%20culture%20of%20the%20company%20or%20to%20make%20the%20right%20connections%20with%20key%20stakeholders.%20It%20places%20most%20of%20the%20emphasis%20in%20the%20recruiting%20process%20on%20evaluating%20people%20for%20raw%20capability%20and%20gives%20relatively%20little%20attention%20to%20cultural%20fit%20or%20adaptability.%3C%2Fli%3E%0A%09%3Cli%3EThe%20company%20often%20places%20external%20hires%20in%20situations%20where%20they%20are%20set%20up%20to%20fail.%20It%20tells%20newly%20hired%20executives%2C%20for%20example%2C%20that%20they%20are%20being%20hired%20for%20their%20ability%20to%20bring%20new%20ideas%20to%20the%20company%2C%20encouraged%20to%20try%20to%20make%20change%20happen%2C%20but%20not%20adequately%20supported%20or%20protected.%20So%20they%20are%20attacked%20and%20destroyed%20by%20the%20organization%u2019s%20immune%20system.%3C%2Fli%3E%0A%09%3Cli%3EThe%20organization%20views%20executive%20coaching%20as%20remedial%20rather%20than%20developmental.%20The%20only%20people%20who%20receive%20coaching%2C%20if%20anyone%20does%2C%20are%20the%20ones%20already%20in%20serious%20trouble.%3C%2Fli%3E%0A%3C%2Ful%3E%0A%3Cp%3EUnsurprisingly%2C%20sink-or-swim%20organizations%20have%20high%20rates%20of%20failure%20in%20onboarding%20outside%20hires.%20The%20associated%20costs%20for%20this%20approach%20are%20very%20high%2C%20and%20not%20just%20in%20terms%20of%20salary%20and%20recruiting%20fees.%20There%20also%20can%20be%20very%20high%20opportunity%20costs%20associated%20with%20having%20someone%20leading%20a%20business%20in%20an%20ineffective%20way%2C%20undercutting%20performance%20and%20disrupting%20the%20work%20of%20many%20others%20in%20the%20organization.%0A%3C%2Fp%3E%0A%3Cp%3EFor%20internal%20promotions%2C%20outright%20derailment%20is%20relatively%20uncommon%2C%20even%20in%20sink-or-swim%20cultures.%20In%20part%2C%20this%20is%20because%20it%u2019s%20difficult%20for%20companies%20to%20promote%20people%20and%20then%20admit%20that%20they%20made%20mistakes%2C%20even%20if%20leaders%20in%20question%20are%20failing%20to%20meet%20expectations.%20But%20the%20costs%2C%20in%20terms%20of%20failures%20of%20new%20leaders%20to%20thrive%2C%20are%20still%20significant.%0A%3C%2Fp%3E%0A%3Cp%3EAre%20you%20in%20a%20sink-or-swim%20culture%3F%20If%20not%2C%20what%20are%20you%20and%20your%20organization%20doing%20to%20avoid%20it%3F%3C%2Fp%3E', '12', '4' )");
