/**** Begin Favorites related storage and retrieval functions ****/

function GetFavoritesList() {
    var favoritesList = localStorage.getItem('favoritesList');
    if (!favoritesList) {
        //alert('favoritesList was not found in localStorage');
        localStorage.setItem('favoritesList', JSON.stringify([]));
        favoritesList = localStorage.getItem('favoritesList');
    }
    favoritesList = JSON.parse(favoritesList);
    //alert('favorites list is [' + favoritesList + '] and it is of type ' + typeof favoritesList);
    return(favoritesList);
}

function AddToFavorites(articleId) {
    //alert('adding [' + articleId + '] to list');
    var list = GetFavoritesList();
    list.push(articleId);
    localStorage.setItem('favoritesList', JSON.stringify(list));
}

function ArticleIsFavorite(articleId) {
    var list = GetFavoritesList();
    var favorite = false;
    if (list.indexOf(articleId) != -1) {
        //alert('the list [' + list + '] contains the article id [' + articleId + ']');
        favorite = true;
    }
    return(favorite);
}

function RemoveFromFavorites(articleId) {
    var favoritesList = GetFavoritesList();
    var index = favoritesList.indexOf(articleId);
    //alert('favoritesList before article removal [' + favoritesList + ']');
    if (index != -1) {
        favoritesList.splice(index, 1); // remove 1 item from index
        //alert('favoritesList after article removal [' + favoritesList + ']');
        localStorage.setItem('favoritesList', JSON.stringify(favoritesList));
    }
}
